import React, { useState, useEffect } from 'react';
import './App.css';
import MapComponent from './components/MapComponent';

function App() {
  const [siteData, setSiteData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    // Load site data from JSON file
    const loadSiteData = async () => {
      try {
        const response = await fetch('/site_data.json');
        if (!response.ok) {
          throw new Error('Failed to load site data');
        }
        const data = await response.json();
        setSiteData(data);
        setLoading(false);
      } catch (err) {
        setError(err.message);
        setLoading(false);
      }
    };

    loadSiteData();
  }, []);

  const formatCurrency = (num) => {
    return `$${num.toLocaleString()}`;
  };

  const formatDateTime = (timestamp) => {
    return new Date(timestamp).toLocaleString();
  };

  if (loading) {
    return (
      <div className="App">
        <div className="App-header">
          <h1 className="App-title">üîÑ Loading MARA Data...</h1>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="App">
        <div className="App-header">
          <h1 className="App-title">‚ùå Error Loading Data</h1>
          <p className="App-subtitle">{error}</p>
          <p style={{ marginTop: '20px', fontSize: '1rem' }}>
            Please run: <code>python export_map_data.py</code> to generate the site data.
          </p>
        </div>
      </div>
    );
  }

  return (
    <div className="App">
      <div className="App-header">
        <h1 className="App-title">‚ö° MARA Geographic Optimizer</h1>
        <p className="App-subtitle">
          Real-time mining optimization across {siteData.total_sites} locations
        </p>
      </div>

      <div className="map-container">
        <MapComponent siteData={siteData} />

        {/* Map Controls */}
        <div className="map-controls">
          <div className="price-info">
            <div>‚ö° Energy: ${siteData.current_prices.energy.toFixed(4)}</div>
            <div>üîó Hash: ${siteData.current_prices.hash.toFixed(4)}</div>
            <div>ü™ô Token: ${siteData.current_prices.token.toFixed(4)}</div>
            <div style={{ fontSize: '0.8rem', color: '#999', marginTop: '5px' }}>
              Updated: {formatDateTime(siteData.timestamp)}
            </div>
          </div>

          <div className="total-profit">
            üí∞ Total Profit: {formatCurrency(siteData.total_profit)}
          </div>

          <div style={{ marginTop: '10px', fontSize: '0.9rem', color: '#ccc' }}>
            üí° Hover markers for quick info<br/>
            üñ±Ô∏è Click markers for detailed view
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
